INSERT INTO `notification_template_version` (`id`, `notification_template_id`, `subject`, `content`, `created_timestamp`) VALUES (13,'APPLICATION_CONFIRM_INTERVIEW_ARRANGEMENTS_NOTIFICATION_INTERVIEWEE','Application %1$s for UCL %2$s - Interview Confirmation','<!DOCTYPE html PUBLIC \"-//W3C//DTD XHTML 1.0 Transitional//EN\" \"http://www.w3.org/TR/xhtml1/DTD/xhtml1-transitional.dtd\">\r <html xmlns=\"http://www.w3.org/1999/xhtml\">\r  \r  <head>\r    <meta http-equiv=\"Content-Type\" content=\"text/html; charset=utf-8\" />\r     <title>Untitled Document</title>\r  </head>\r \r  <body>\r    <table width=\"600\" border=\"0\" cellspacing=\"0\" cellpadding=\"0\">\r      <tr>\r          <td colspan=\"3\">\r            <img src=\"${host}/pgadmissions/design/default/images/email/header.jpg\" width=\"600\" height=\"160\" alt=\"Prism: A Spectrum of Postgraduate Research Opportunities\" />\r           </td>\r         </tr>\r         <tr>\r          <td width=\"50\">\r             <img src=\"${host}/pgadmissions/design/default/images/shim.gif\" width=\"50\" height=\"10\" alt=\"\" />\r           </td>\r           <td width=\"500\">\r              <h1 style=\"font-size: 12pt;\">\r                 <font face=\"Arial, Helvetica, sans-serif\" color=\"#0055A1\">Dear ${application.applicant.firstName},</font>\r               </h1>\r               <p>\r                 <font face=\"Arial, Helvetica, sans-serif\" size=\"2\">\r                 <#if (previousStage.displayValue() = \"Interview\")>\r                   We can confirm that your Application ${application.applicationNumber} for UCL <#if application.researchHomePage??><a href=\"${application.researchHomePage}\">${application.program.title}</a><#else>${application.program.title}</#if> has been advanced to a further stage of interview.\r               <#else>\r                     We are pleased to confirm that your Application ${application.applicationNumber} for UCL <#if application.researchHomePage??><a href=\"${application.res